mapper.select:
SELECT
        soc.customer_id     AS customerId,
        COUNT(soc.order_id) AS newQty
FROM 	sd_order_condition soc
WHERE 	soc.quarter = #{quarter}
        AND soc.create_date >= #{beginDate}
        AND  soc.create_date < #{endDate}
GROUP BY customerId
LIMIT 10000

SELECT
        COUNT(soc.order_id) AS newQty
FROM 	sd_order_condition soc
WHERE 	soc.quarter = #{quarter}
        AND soc.customer_id = #{customerId}
        AND soc.create_date >= #{beginDate}
        AND  soc.create_date < #{endDate}

SELECT
      COUNT(DISTINCT fcc.order_id)  AS completedQty
FROM  fi_customercharge  fcc
WHERE fcc.quarter = #{quarter}
      AND fcc.customer_id = #{customerId}
      AND fcc.create_date >= #{beginDate} AND  fcc.create_date < #{endDate}
      AND fcc.charge_order_type = 0 AND fcc.del_flag = 0

SELECT
      COUNT(soc.order_id) AS returnedQty
FROM  sd_order_condition soc
WHERE soc.status = 90
      AND soc.customer_id = #{customerId}
      AND soc.close_date >= #{beginDate} AND  soc.close_date < #{endDate}

SELECT
      COUNT(soc.order_id) AS returnedQty
FROM  sd_order_condition soc
WHERE soc.status = 100
      AND soc.customer_id = #{customerId}
      AND soc.close_date >= #{beginDate} AND  soc.close_date < #{endDate}

SELECT
        SUM(fcc.amount)   AS rechargeAmount
FROM 	fi_customercurrency fcc
WHERE 	fcc.quarter = #{quarter}
        AND fcc.customer_id = #{customerId}
        AND fcc.create_date >= #{beginDate} AND  fcc.create_date < #{endDate} 
        AND  fcc.action_type <= 20

SELECT
        SUM(fcc.service_charge+fcc.express_charge+fcc.travel_charge+fcc.material_charge+fcc.other_charge) AS completedOrderCharge,
        SUM(fcc.time_liness_charge) AS timelinessCharge,
        SUM(fcc.urgent_charge)      AS urgentCharge
FROM    fi_customercharge fcc
WHERE 	fcc.quarter = #{quarter}
        AND fcc.customer_id = #{customerId}
        AND fcc.create_date >= #{beginDate} AND  fcc.create_date < #{endDate} 
        AND fcc.charge_order_type = 0
        AND fcc.del_flag = 0

SELECT
        SUM(fcc.service_charge+fcc.express_charge+fcc.travel_charge+fcc.material_charge+fcc.other_charge) AS writeOffCharge
FROM    fi_customercharge fcc
WHERE 	fcc.quarter = #{quarter}
        AND fcc.customer_id = #{customerId}
        AND fcc.create_date >= #{beginDate} AND  fcc.create_date < #{endDate} 
        AND fcc.charge_order_type = 1
        AND fcc.del_flag = 0

SELECT
        SUM(fcc.amount)     AS balance
FROM 	fi_customercurrency fcc
WHERE 	fcc.customer_id = #{customerId}
        AND fcc.action_type >= 10  AND  fcc.action_type <= 50 
        AND  fcc.create_date < #{endDate}

mapper.delete:
DELETE FROM rpt_customer_finance_monthly
WHERE system_id = #{systemId}
      AND yearmonth = #{yearmonth}

mapper.update:
UPDATE rpt_customer_finance_monthly
last_month_balance = #{lastMonthBalance}, recharge_amount = #{rechargeAmount}, completed_order_charge = #{completedOrderCharge}, write_off_charge = #{writeOffCharge}, timeliness_charge = #{timelinessCharge}, urgent_charge = #{urgentCharge}, balance =#{balance}, block_amount = #{blockAmount}, update_date = #{updateDt}
WHERE id = #{id}

mapper.delete:
DELETE FROM rpt_customer_order_qty_monthly
WHERE system_id = #{systemId}
      AND yearmonth = #{yearmonth}

mapper.update:
UPDATE rpt_customer_order_qty_monthly
last_month_uncompleted_qty = #{lastMonthUncompletedQty}, new_qty = #{newQty}, completed_qty = #{completedQty}, returned_qty = #{returnedQty}, cancelled_qty = #{cancelledQty}, uncompleted_qty = #{uncompletedQty}, update_date = #{updateDt}
WHERE id = #{id}
