mapper.select:
SELECT
soc.order_no , so.shop_id, so.parent_biz_order_id, soc.create_by AS "orderCreateById",
so.orderitem_json AS "orderItemPb", so.data_source AS "dataSourceId",
fcc.amount AS "blockAmount", fcc.remarks, fcc.currency_type,
fcc.currency_no
FROM fi_customercurrency_block fcc
left join sd_order_condition soc ON soc.order_no = fcc.currency_no
left join sd_order_head so on so.id = soc.order_id

    INNER JOIN md_customer_sales mcs on mcs.customer_id = fcc.customer_id

        inner join sys_user_sub as sus on sus.sub_user_id = mcs.sales_id and sus.type = 7


WHERE
    fcc.quarter = #{quarter}


            AND fcc.customer_id = #{customerId}




                    and sus.user_id = #{saleId}


                    and mcs.sales_id = #{saleId}




    and fcc.create_date >= #{beginDate}
    and  fcc.create_date <= #{endDate}

SELECT
soc.order_no , so.shop_id, so.parent_biz_order_id, soc.create_by AS "orderCreateById",
so.orderitem_json AS "orderItemPb", so.data_source AS "dataSourceId",
fcc.amount AS "blockAmount", fcc.remarks, fcc.currency_type,
fcc.currency_no
FROM fi_customercurrency_block fcc
left join sd_order_condition soc ON soc.order_no = fcc.currency_no
left join sd_order_head so on so.id = soc.order_id

    INNER JOIN md_customer_sales mcs on mcs.customer_id = fcc.customer_id

        inner join sys_user_sub as sus on sus.sub_user_id = mcs.sales_id and sus.type = 7


WHERE
    fcc.quarter = #{quarter}


            AND fcc.customer_id = #{customerId}




                    and sus.user_id = #{saleId}


                    and mcs.sales_id = #{saleId}




    and fcc.create_date >= #{beginDate}
    and  fcc.create_date <= #{endDate}

SELECT
COUNT(0)
FROM fi_customercurrency_block fcc

    INNER JOIN md_customer_sales mcs on mcs.customer_id = fcc.customer_id

        inner join sys_user_sub as sus on sus.sub_user_id = mcs.sales_id and sus.type = 7



    fcc.quarter = #{quarter}
    AND fcc.create_date >= #{beginDate}
    AND  fcc.create_date <= #{endDate} 


            AND fcc.customer_id = #{customerId}




                    and sus.user_id = #{saleId}


                    and mcs.sales_id = #{saleId}
