mapper.select:
SELECT
   customer_id AS customerId, servicepoint_id AS servicePointId, kefu_id AS keFuId, area_id AS areaId,
   order_no AS orderNo, workcard_id AS workCardId, parent_biz_order_id AS parentBizOrderId,
   create_date AS createDt, `status` AS "status.value", servicepoint_praise_fee AS  praiseFee,
   apply_servicepoint_praise_fee AS applyPraiseFee, engineer_id,
   user_name AS userName, user_address AS userAddress, user_phone AS userPhone
FROM cp_order_praise


    kefu_id = #{keFuId}


    AND servicepoint_id = #{servicePointId}


    AND province_id=#{areaId}


    and city_id=#{areaId}


    AND area_id=#{areaId}


    AND status = #{status}


    AND create_date >= #{beginDate}


    AND create_date <= #{endDate}

SELECT
COUNT(0)
FROM cp_order_praise


        kefu_id = #{keFuId}


        AND servicepoint_id = #{servicePointId}


        AND province_id=#{areaId}


        and city_id=#{areaId}


        AND area_id=#{areaId}


        AND status = #{status}


        AND create_date >= #{beginDate}


        AND create_date <= #{endDate}
