mapper.select:
SELECT
area_id AS areaId,order_no AS orderNo, workcard_id AS workCardId, parent_biz_order_id AS parentBizOrderId,
create_date AS createDt, `status` AS "status.value", servicepoint_praise_fee AS  praiseFee,engineer_id,
apply_servicepoint_praise_fee AS applyPraiseFee,
user_name AS userName, user_address AS userAddress, user_phone AS userPhone
FROM cp_order_praise


        create_date >= #{beginDate}


        AND create_date <= #{endDate} 


        AND servicepoint_id = #{servicePointId}


        AND status = #{status}

SELECT
COUNT(0)
FROM cp_order_praise


         create_date >= #{beginDate}


        AND create_date <= #{endDate} 


        AND servicepoint_id = #{servicePointId}


        AND status = #{status}
