mapper.select:
SELECT count(0)
FROM rpt_created_order
WHERE order_create_date >= #{startDate}
AND  order_create_date <= #{endDate}
AND system_id = #{systemId}

    and quarter = #{quarter}

SELECT
product_category_id AS "aElement",
count(0) AS "bElement"
FROM rpt_created_order
WHERE order_create_date >= #{startDate}
AND  order_create_date <= #{endDate}
AND system_id = #{systemId}

    and quarter = #{quarter}

GROUP BY product_category_id
