mapper.select:
SELECT mcs.customer_id as customerId,
    mcs.sales_id as salesId
FROM rpt_md_customer_sales mcs

    inner join rpt_sys_user_sub as sus on sus.sub_user_id = mcs.sales_id and sus.type = 7


    mcs.system_id = #{systemId}



                and sus.user_id= #{salesId}


                and mcs.sales_id=#{salesId}

SELECT
a.customer_id as customerId , sum(a.last_month_uncompleted_qty) as preNoFinishQty, sum(a.new_qty) as newQty, sum(a.completed_qty) as finishQty,
sum(a.returned_qty) as returnQty, sum(a.cancelled_qty) as cancelQty, sum(a.uncompleted_qty) as noFinishQty
FROM rpt_customer_order_qty_monthly a

    a.system_id = #{systemId}
    AND a.yearmonth = #{yearMonth}



                AND a.product_category_id = #{productCategoryIds[0]}


                AND a.product_category_id IN

                    #{item}







              AND  a.customer_id = #{customerIds[0]}


                AND  a.customer_id IN

                    #{customerId}





group by customerId

SELECT
a.customer_id as customerId , sum(a.last_month_balance) as preBalance, sum(a.recharge_amount) as rechargeAmount, sum(a.completed_order_charge) as orderPaymentAmount,
sum(a.write_off_charge) as diffCharge, sum(a.timeliness_charge) as customerTimeLinessCharge, sum(a.urgent_charge) as customerUrgentCharge,
sum(a.balance) as balance,  sum(a.block_amount) as block_amount, sum(a.praise_fee) as praiseFee
FROM rpt_customer_finance_monthly a

    a.system_id = #{systemId}
    AND a.yearmonth = #{yearMonth}



                AND a.product_category_id = #{productCategoryIds[0]}


                AND a.product_category_id IN

                    #{item}







                AND   a.customer_id = #{customerIds[0]}


                AND  a.customer_id IN

                    #{customerId}





group by customerId

SELECT
a.id as customerId , a.credit as currentCredit, a.deposit as currentDeposit
FROM md_customer_finance a




                a.id = #{customerIds[0]}


                  a.id IN

                    #{customerId}

SELECT
count(0)
FROM rpt_customer_finance_monthly a

    a.system_id = #{systemId}
    AND a.yearmonth = #{yearMonth}



                AND a.product_category_id = #{productCategoryIds[0]}


                AND a.product_category_id IN

                    #{item}





    AND   a.customer_id = #{customerId}
