From rpt_kefu_complete_time
WHERE order_create_date >= #{beginDate}
AND  order_create_date <= #{endDate}
AND  efficiency_flag > '40'
AND  system_id = #{systemId}



            AND product_category_id = #{productCategoryIds[0]}


            AND product_category_id IN

                #{item}





    AND quarter = #{quarter}

GROUP BY dayIndex
LIMIT 10000

