mapper.select:
SELECT
kefu_id AS "keFuId",
day_index AS "dayIndex",
count(id) AS orderSum
FROM
rpt_created_order

    system_id = #{systemId}
    AND order_create_date >= #{startDate}
    AND  order_create_date <= #{endDate}

        and kefu_id = #{keFuId}




                and product_category_id = #{productCategoryIds[0]}


                and product_category_id in

                    #{productCategoryIds}





        and quarter = #{quarter}


group by keFuId,dayIndex
LIMIT 8000

SELECT  soc.kefu_id AS "keFuId",
count(soc.order_id) AS pendingQty
FROM    sd_order_condition soc
WHERE 	soc.close_date is null and charge_flag = 0
and soc.pending_type > 0 and soc.appointment_date > #{endDate}

    and soc.kefu_id = #{keFuId}




            and soc.product_category_id = #{productCategoryIds[0]}


            and soc.product_category_id in

                #{productCategoryIds}




GROUP BY keFuId
LIMIT 2000

SELECT  soc.kefu_id AS "keFuId",
count(soc.order_id) AS noGradedQty
FROM    sd_order_condition soc
WHERE 	soc.close_date is null  and charge_flag = 0

    and soc.kefu_id = #{keFuId}




            and soc.product_category_id = #{productCategoryIds[0]}


            and soc.product_category_id in

                #{productCategoryIds}




GROUP BY keFuId
LIMIT 2000

SELECT
COUNT(0)
FROM
rpt_created_order

    system_id = #{systemId}
    AND order_create_date >= #{startDate}
    AND  order_create_date <= #{endDate}



                and kefu_id  = #{keFuIds[0]}


                and kefu_id  in

                    #{keFuIds}







                and product_category_id = #{productCategoryIds[0]}


                and product_category_id in

                    #{productCategoryIds}





        and quarter = #{quarter}

SELECT
count(soc.order_id)
FROM    sd_order_condition soc
