and soc.pending_type > 0 and soc.appointment_date > #{endDate}



            and soc.kefu_id  = #{keFuIds[0]}


            and soc.kefu_id  in

                #{keFuIds}







            and soc.product_category_id = #{productCategoryIds[0]}


            and soc.product_category_id in

                #{productCategoryIds}

SELECT
count(soc.order_id)
FROM    sd_order_condition soc
