mapper.select:
select
date_format(fsw.pay_date, '%Y-%m-%d') AS payDate,
fsw.bank as bankOwner, fsw.payment_type as bankNo,
sum(fsw.pay_amount) as payAmount,
sum(fsw.platform_fee) as platformFee
from fi_servicepoint_withdraw  fsw

    fsw.del_flag = 0
    and fsw.status = 40
    and  fsw.pay_date >= #{beginDate}
    and   fsw.pay_date <= #{endDate} 

        and fsw.payment_type = #{paymentType}


        and fsw.bank = #{bank}


    AND fsw.quarter = #{quarter}


group by fsw.bank, fsw.payment_type, payDate;

select
count(0)
from fi_servicepoint_withdraw  fsw

    fsw.del_flag = 0
    and fsw.status = 40
    and  fsw.pay_date >= #{beginDate}
    and   fsw.pay_date <= #{endDate} 

        and fsw.payment_type = #{paymentType}


        and fsw.bank = #{bank}


        AND fsw.quarter = #{quarter}
