mapper.select:
SELECT
sum(frequent_qty) AS "frequentQty",
sum(trial_qty) AS "trialQty"
FROM rpt_servicepoint_qty
WHERE
system_id = #{systemId}
and create_date >= #{beginDate}
AND  create_date <= #{endDate}
and product_category_id = #{productCategoryId}

SELECT
product_category_id,
sum(frequent_qty) AS "frequentQty",
sum(trial_qty) AS "trialQty"
FROM rpt_servicepoint_qty
WHERE
system_id = #{systemId}
and create_date >= #{beginDate}
AND  create_date <= #{endDate}

    and product_category_id in

        #{productCategoryIds}


GROUP BY product_category_id

SELECT
product_category_id,
sum(auto_plan_qty) AS "autoPlanQty"
FROM rpt_servicepoint_qty
WHERE
system_id = #{systemId}
and create_date >= #{beginDate}
AND  create_date <= #{endDate}

    and product_category_id in

        #{productCategoryIds}


GROUP BY product_category_id

mapper.delete:
DELETE FROM rpt_servicepoint_qty
WHERE   system_id = #{systemId}
AND create_date >= #{startDate}
AND  create_date <= #{endDate}
