mapper.select:
SELECT
servicepoint_street AS "servicePointStreet",
frequent_servicepoint AS "frequentServicePoint",
trial_servicepoint AS "trialServicePoint",
without_servicepoint AS "withoutServicePoint"
FROM rpt_servicepoint_street_qty
WHERE
system_id = #{systemId}
and create_date >= #{beginDate}
AND  create_date <= #{endDate}
and product_category_id = #{productCategoryId}

SELECT
product_category_id,
sum(frequent_servicepoint) AS "frequentServicePoint",
sum(trial_servicepoint) AS "trialServicePoint",
sum(without_servicepoint) AS "withoutServicePoint"
FROM rpt_servicepoint_street_qty
WHERE
system_id = #{systemId}
AND create_date >= #{beginDate}
AND  create_date <= #{endDate}

    AND product_category_id in

        #{productCategoryIds}


GROUP BY product_category_id

SELECT
auto_plan_street AS "autoPlanStreet",
auto_plan_without_frequent AS "autoPlanWithoutFrequent",
frequent_without_auto_plan AS "frequentWithoutAutoPlan"
FROM rpt_servicepoint_street_qty
WHERE
system_id = #{systemId}
and create_date >= #{beginDate}
AND  create_date <= #{endDate}
and product_category_id = #{productCategoryId}

mapper.delete:
DELETE FROM rpt_servicepoint_street_qty
WHERE   system_id = #{systemId}
AND create_date >= #{startDate}
AND  create_date <= #{endDate}
